mapper.insert:
	INSERT INTO 
			ENGLAND_TEAM(
			TEAM_ID,
			NAME,
			SHORT_CODE,
			LOGO
		)VALUES(
			#{team_id},
			#{name},
			#{short_code},
			#{logo}
		)
		ON DUPLICATE KEY UPDATE
        	TEAM_ID = #{team_id}
           , NAME = #{name}
           , SHORT_CODE = #{short_code}
           , LOGO =  #{logo}

	INSERT INTO 
			Present_EPL_Season(
			IS_CURRENT,
			SEASON_NAME,
			START_DATE,
			END_DATE
		)VALUES(
			#{is_current},
			#{season_name},
			#{start_date},
			#{end_date}
		)
		ON DUPLICATE KEY UPDATE
        	IS_CURRENT = #{is_current}
           , SEASON_NAME = #{season_name}
           , START_DATE = #{start_date}
           , END_DATE =  #{end_date}
